package JDBC;

import Interfaces.SuppliesManager;

import POJO.Supply;

import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.Statement;
import java.util.ArrayList;


public class JDBCSuppliesManager implements SuppliesManager{

	private JDBCManager manager;
	
	public JDBCSuppliesManager(JDBCManager m){
		
		this.manager = m;
	}
	
	//The id variable is not included as a parameter in  INSERT INTO because it is
	//generated by the database with an AUTOINCREMENT
	@Override
	public void addSupply (Supply s) throws Exception{
		
		String sql= "INSERT INTO supplies (name, amount) "
				+ " VALUES(?,?)";
		
		PreparedStatement prep= this.manager.getConnection().prepareStatement(sql);
		
		prep.setString(1, s.getItem_name());
		prep.setInt(2, s.getItem_amount());
		
		prep.executeUpdate();
		
	}
	
	//Returns a list with the supplies. It can be used to print the supplies in the menu
	@Override
	public ArrayList<Supply> listAllSupplies() throws Exception{
		
		
		ArrayList <Supply> supplies= new ArrayList<>();
	
		Statement stmt= this.manager.getConnection().createStatement();	
		
		String sql="SELECT * FROM supplies";
		
		ResultSet rs = stmt.executeQuery(sql);
		
		while (rs.next()){
			
			int id = rs.getInt("id");
			String name = rs.getString("name");
			int amount = rs.getInt("amount");
			
			Supply supply = new Supply(id, name, amount);
			
			supplies.add(supply);
		}
		
		rs.close();
		stmt.close();

		return supplies;
									
	}
	
}
